CommandButton.CSS:
.sb-commandbar-button {
  border-width: 4px;
  border-style: solid;
  background-color: "[theme: themeLighter]";
  border-color: "[theme: white]";
  border-radius: 8px;
  color: "[theme: themePrimary]";
}

.sb-commandbar-button.is-disabled {
  border-color: "[theme: white]";
}

.sb-commandbar-button:hover:not(:active) {
  background-color: "[theme: themeLighterAlt]";
}

.sb-commandbar-icon {
  color: "[theme: themeDarker]";
}
